let store = {
    _state: {
        profilePage: {
            posts: [
                { id: 1, message: 'Hi, how are you?', likesCount: 3 },
                { id: 2, message: 'It\'s my first post', likesCount: 6 },
                { id: 3, message: 'It\'s my second post', likesCount: 0 },
                { id: 4, message: 'It\'s my second post', likesCount: 10 },
                { id: 5, message: 'It\'s my second post', likesCount: 4 }
            ],
            newPostText: 'blablabla'
        },
        dialogsPage: {
            dialogs: [
                { id: 1, name: 'Janna' },
                { id: 2, name: 'Sasha' },
                { id: 3, name: 'Kata' },
                { id: 4, name: 'Bard' },
                { id: 5, name: 'Lulu' }
            ],
            messages: [
                { id: 1, message: 'Hello' },
                { id: 2, message: 'This day is butiful!' },
                { id: 3, message: 'How are you?' }
            ],
            newMessageText: 'jjkjkjkj'
        },
        sitebar: {
            onlineFriends: [
                { id: 1, name: 'Kata' },
                { id: 2, name: 'Bard' },
                { id: 3, name: 'Lulu' }
            ]
        }
    },
    getState() {
        return this._state;
    },
    _callSubscriber() {
        console.log('state change');
    },
    updateNewPostText(newText) {
        this._state.profilePage.newPostText = newText;

        this._callSubscriber(this._state);
    },
    addPost() {
        debugger
        let newPost = {
            id: 5, 
            message: this._state.profilePage.newPostText,
            likesCount: 0
        };

        this._state.profilePage.posts.push(newPost);
        this._state.profilePage.newPostText = '';
        this._callSubscriber(this._state);
    },
    updateNewMessageText(newDia) {
        this._state.dialogsPage.newMessageText = newDia;
        console.log(newDia)
        this._callSubscriber(this._state);
    },
    addMessage() {
        let newMes = {
            id: 4, 
            message: this._state.dialogsPage.newMessageText,
        };

        this._state.dialogsPage.messages.push(newMes);
        this._state.dialogsPage.newMessageText = '';
        this._callSubscriber(this._state);
    },
    subscribe(observer) {
        this._callSubscriber = observer;
    }
}




window.state = store;


export default store;
