const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';

let initialState = {
    users: [
        { id: 1, followed: false, fullName: 'Leona', status: 'I like cats', location: {city: 'Moscow', country: 'Russia'} },
        { id: 2, followed: true, fullName: 'Bard', status: 'I like dogs', location: {city: 'Prague', country: 'CzechRepublic'} },
        { id: 3, followed: true, fullName: 'Lulu', status: 'I like sport', location: {city: 'Kiev', country: 'Ukrain'} },
        { id: 4, followed: false, fullName: 'Swan', status: 'I am a boss ', location: {city: 'Orenburg', country: 'Russia'} },
    ]
};

const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map ( u => {
                    if (u.id === action.userId) {
                        return {...u, followed: true}
                    }
                    return u;
                })
            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map ( u => {
                    if (u.id === action.userId) {
                        return {...u, followed: false}
                    }
                    return u;
                })
            }
        case SET_USERS: {
            return {...state, users: [...state.users, ...action.users] }
        }
        default: 
            return state;
    }
}

export const followAC = (userId) => ({type: FOLLOW, userId})
export const unfollowAC = (userId) => ({type: UNFOLLOW, userId})
export const setUsersAC = (users) => ({type: SET_USERS, users})

export default usersReducer;
