import React from 'react';
import classes from './Dialogs.module.css';
import Message from './Message/Message';
import DialogItem from './DialogItem/DialogItem';
import { addMessageActionCreator, updateNewMessageTextActionCreator } from '../../redux/Store';
import Dialogs from './Dialogs';



const DialogsContainer = (props) => {

    let state = props.store.getState().dialogsPage;

    let addMessage = () => {
        props.store.dispatch(addMessageActionCreator());
    }

    let onMessageChange = (mes) => {
        props.store.dispatch(updateNewMessageTextActionCreator(mes));
    }


    return (
        <Dialogs addMessage={addMessage} updateNewMessageText={onMessageChange} dialogsPage={state} />
    );
}


export default DialogsContainer;
