import * as axios from 'axios';
import React from 'react';
import classes from './Users.module.css';

let Users = (props) => {
    if (props.users.length === 0) {
        axios
            .get('https://social-network.samuraijs.com/api/1.0/users')
            .then(response => {
                debugger;
                props.setUsers(response.data.items)
            })
        }
        // props.setUsers([
        //     { id: 1, photoUrl:'https://i.pinimg.com/564x/7d/ea/29/7dea29735f744f7c9f09745b7b62274a.jpg',
        //          followed: false, fullName: 'Leona', status: 'I like cats', location: {city: 'Moscow', country: 'Russia'} },
        //     { id: 2, photoUrl:'https://i.pinimg.com/564x/15/99/f6/1599f61d5ec1edd4e66ef5a24762e03e.jpg',
        //          followed: true, fullName: 'Bard', status: 'I like dogs', location: {city: 'Prague', country: 'CzechRepublic'} },
        //     { id: 3, photoUrl:'https://i.pinimg.com/564x/18/67/8a/18678a1602e67ddd8ed6f22b2089ea0c.jpg', 
        //          followed: true, fullName: 'Lulu', status: 'I like sport', location: {city: 'Kiev', country: 'Ukrain'} },
        //     { id: 4, photoUrl:'https://i.pinimg.com/564x/1e/e8/b0/1ee8b0e85bf33e281e1fb639dc2e88ab.jpg', 
        //         followed: false, fullName: 'Swan', status: 'I am a boss ', location: {city: 'Orenburg', country: 'Russia'} },
        //     ]
        // )

    return <div>
        {
            props.users.map (u => <div className={classes.pad} key={u.id}>
                <span>
                    <div className={classes.uPhoto}>
                        <img src={u.photoUrl}/>
                    </div>
                    <div>
                        {u.followed ? <button onClick={() => (props.unfollow(u.id))} > unfollow</button> : <button onClick={() => (props.follow(u.id))} > follow</button>}
                    </div>
                </span>
                <span>
                    <div>{u.fullName}</div>
                    <div>{u.status}</div>

                    <div>{u.location.country}</div>
                    <div>{u.location.city}</div>
                </span>
            </div>)
        }
    </div>

}

export default Users;
