let renderEntireTree = () => {
    console.log('state change');
};

let state = {
    profilePage: {
        posts: [
            { id: 1, message: 'Hi, how are you?', likesCount: 3 },
            { id: 2, message: 'It\'s my first post', likesCount: 6 },
            { id: 3, message: 'It\'s my second post', likesCount: 0 },
            { id: 4, message: 'It\'s my second post', likesCount: 10 },
            { id: 5, message: 'It\'s my second post', likesCount: 4 }
        ],
        newPostText: 'blablabla'
    },
    dialogsPage: {
        dialogs: [
            { id: 1, name: 'Janna' },
            { id: 2, name: 'Sasha' },
            { id: 3, name: 'Kata' },
            { id: 4, name: 'Bard' },
            { id: 5, name: 'Lulu' }
        ],
        messages: [
            { id: 1, message: 'Hello' },
            { id: 2, message: 'This day is butiful!' },
            { id: 3, message: 'How are you?' }
        ],
        newMessageText: 'jjkjkjkj'
    },
    sitebar: {
        onlineFriends: [
            { id: 1, name: 'Kata' },
            { id: 2, name: 'Bard' },
            { id: 3, name: 'Lulu' }
        ]
    }
}

export const updateNewPostText = (newText) => {
    state.profilePage.newPostText = newText;

    renderEntireTree(state);
}

export const addPost = () => {
    let newPost = {
        id: 5, 
        message: state.profilePage.newPostText,
        likesCount: 0
    };

    state.profilePage.posts.push(newPost);
    state.profilePage.newPostText = '';
    renderEntireTree(state);
}

export const updateNewMessageText = (newDia) => {
    state.dialogsPage.newMessageText = newDia;
    console.log(newDia)
    renderEntireTree(state);
}

export const addMessage = () => {
    let newMes = {
        id: 4, 
        message: state.dialogsPage.newMessageText,
    };

    state.dialogsPage.messages.push(newMes);
    state.dialogsPage.newMessageText = '';
    renderEntireTree(state);
}

export const subscribe = (observer) => {
    renderEntireTree = observer;
}

export default state;
