import React from 'react';
import classes from './Users.module.css';
import userPhoto from '../../assets/images/user.jpeg';


const Users = (props) => {
    let pp = props;
    let pagesCount = Math.ceil (pp.totalUsersCount / pp.pageSize);

        let pages = [];

        let endPage;
        let startPage;

        startPage = 1;
        endPage = pagesCount;

        if((pp.currentPage) >= (startPage + 6)){
            pages.push(1);
        }
        if((pp.currentPage) >= (startPage + 7)){
            pages.push('...');
        }

        for (let i=1; i <= pagesCount; i++) {
            if( pp.currentPage <= (i+5) && pp.currentPage >= (i-5))
            pages.push(i);
        }

        if((pp.currentPage) <= (endPage - 7)){
            pages.push('...');
        }
        if((pp.currentPage) <= (endPage - 6)){
            pages.push(pagesCount);
        }
    return (

        <div>

            <div className={classes.cursor}>
                {pages.map(p => {
                    if (typeof p == 'number') {
                        return <span className={`${classes.pagItem} ${pp.currentPage === p ? classes.selectPage : ''}`}
                            onClick={(e) => { pp.onPageChanged(p); }}>{p}</span>
                    } else {
                        return <span className={`${classes.pagDot} ${pp.currentPage === p ? classes.selectPage : ''}`}>{p}</span>
                    }
                })}
            </div>

            {
                this.pp.users.map(u => <div className={classes.pad} key={u.id}>
                    <span>
                        <div className={classes.uPhoto}>
                            <img src={u.photos.small != null ? u.photos.small : userPhoto} />
                        </div>
                        <div>
                            {u.followed ? <button onClick={() => (pp.unfollow(u.id))} > unfollow</button> : <button onClick={() => (pp.follow(u.id))} > follow</button>}
                        </div>
                    </span>
                    <span>
                        <div>{u.name}</div>
                        <div>{u.status}</div>

                        <div>{'u.location.country'}</div>
                        <div>{'u.location.city'}</div>
                    </span>
                </div>)
            }
        </div>
    )
}

export default Users;
