import React from 'react';
import classes from './Dialogs.module.css';
import Message from './Message/Message';
import DialogItem from './DialogItem/DialogItem';

const addMessageActionCreator = () => {
    return {
        type: 'ADD-MESSAGE'
    }
}

const updateNewMessageTextActionCreator = (mes) => {
    return {
        type: 'UPDATE-NEW-MESSAGE-TEXT', newDia: mes
    }
}

const Dialogs = (props) => {

    let dialogsElements = props.state.dialogs.map( d => <DialogItem name={d.name} id={d.id}/> );

    let messagesElements = props.state.messages.map( m => <Message message={m.message}/>);

    let newMessageElement = React.createRef();

    let addMessage = () => {
        props.dispatch(addMessageActionCreator());
    }

    let onMessageChange = () => {
        let mes = newMessageElement.current.value;
        props.dispatch(updateNewMessageTextActionCreator(mes));
    }


    return (
        <div className={classes.dialogs}>
            <div className={classes.dialogsItems}>
                {dialogsElements}
            </div>
            <div className={classes.messages}>
                {messagesElements}
                <textarea ref={newMessageElement} onChange={onMessageChange} value={props.newMessageText} />
                <div>
                    <button onClick={addMessage}>add</button>
                </div>
            </div>
        </div>
    );
}


export default Dialogs;
